import React from "react";
import { ShopContext } from "./Context/Shopcontext";
function Cartfeatures(){
  const{all_products,arr,deleteItem}=React.useContext(ShopContext);
return (
  <div className="cart-main">
  <div className="Cartfeatures">
   <p>Product</p>
   <p>Name</p>
   <p>Price</p>
   <p>Quantity</p>
   <p>Total</p>
   <p>Remove from Cart</p>
  </div>
  <hr/>
  {all_products.map((e)=>{
 if(arr[e.id]>0)
  return(<div className="individual">
 <img src={e.image} alt="pic" className="image-of-product" />
 <p>e.name</p>
 <p>$e.new_price</p>
 <button className="quantity-cart">
{arr[e.id]}
 </button>
<p>{e.new_price*arr[e.id]}</p>
<button className="remove-from-cart" onClick={()=>{deleteItem(e.id)}}></button>
    </div>);
  })}
  </div>

);
}
export default Cartfeatures;
