import React from "react";
import { ShopContext } from "./Context/Shopcontext";

function Cartfeatures() {
  const { all_product, prod, deleteItem } = React.useContext(ShopContext);

  // Check if all_product is defined
  if (!all_product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="cart-main">
      <div className="Cartfeatures">
        <p>Product</p>
        <p>Name</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove from Cart</p>
      </div>
      <hr />
      {all_product.map((e) => {
        if (prod[e.id] > 0)
          return (
            <div className="individual" key={e.id}>
              <img src={e.image} alt="pic" className="image-of-product" />
              <p>{e.name}</p>
              <p>${e.new_price}</p>
              <button className="quantity-cart">{prod[e.id]}</button>
              <p>{e.new_price * prod[e.id]}</p>
              <button
                className="remove-from-cart"
                onClick={() => {
                  deleteItem(e.id);
                }}
              >
                Remove
              </button>
            </div>
          );
        return null;
      })}
    </div>
  );
}

export default Cartfeatures;
