import React from "react";
import all_product from "../../components/objects/Assets/all_product";

export const ShopContext = React.createContext(null);

const getInitialProductState = () => {
  let arr = {};
  for (let i = 0; i < all_product.length; i++) {
    arr[all_product[i].id] = 0;
  }
  return arr;
};

const ShopContextProvider = (props) => {
  const [prod, setProd] = React.useState(getInitialProductState());

  const addItem = (itemid) => {
    setProd((prev) => ({ ...prev, [itemid]: prev[itemid] + 1 }));
  };

  const deleteItem = (itemid) => {
    setProd((prev) => ({ ...prev, [itemid]: prev[itemid] - 1 }));
  };

  return (
    <ShopContext.Provider value={{ all_product, prod, addItem, deleteItem }}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
