import React, { useEffect, useState } from "react";

const ShopContextProvider = (props) => {
  const [prod, setProd] = useState(getproduct());
  const [all_product, setAll_product] = useState([]);
  const [sum, setSum] = useState(0);

  useEffect(() => {
    // Fetch all products and set them to state
    fetch('http://localhost:4000/allproducts')
      .then((response) => response.json())
      .then((data) => {
        console.log(data); 
        setAll_product(data);
      });

    // Fetch user's cart data if authenticated and set to state
    if (localStorage.getItem('auth-token')) {
      fetch('http://localhost:4000/getcart', {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'auth-token': `${localStorage.getItem('auth-token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({}),
      })
      .then((response) => response.json())
      .then((data) => {
        console.log('Cart Data:', data);
        setProd(data);
      })
      .catch((error) => console.error('Error:', error));
    }
  }, []);

  useEffect(() => {
    calculateTotalSum();
  }, [prod]);

  const calculateTotalSum = () => {
    let sum1 = 0;
    for (let i = 0; i < all_product.length; i++) {
      const product = all_product[i];
      if (prod[product.id] > 0) {
        sum1 += product.new_price * prod[product.id];
      }
    }
    setSum(sum1);
  };

  const addItem = (itemId) => {
    fetch('http://localhost:4000/addtocart', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'auth-token': `${localStorage.getItem('auth-token')}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ itemId }),
    })
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      setProd((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    })
    .catch((error) => console.error('Error:', error));
  };

  const deleteItem = (itemId) => {
    fetch('http://localhost:4000/removefromcart', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'auth-token': `${localStorage.getItem('auth-token')}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ itemId }),
    })
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      setProd((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
    })
    .catch((error) => console.error('Error:', error));
  };

  const count = () => {
    let totalCount = 0;
    for (const itemId in prod) {
      if (prod[itemId] > 0) {
        totalCount += prod[itemId];
      }
    }
    return totalCount;
  };

  return (
    <ShopContext.Provider value={{ all_product, prod, addItem, deleteItem, sum, count }}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
